function loadJokes(e){const xhr=new XMLHttpRequest,number=document.querySelector('input[type="number"]').value;xhr.open("GET",`http://api.icndb.com/jokes/random/${number}`,!0),xhr.onload=function(){if(200===this.status){let response=JSON.parse(this.responseText),output="";"success"===response.type?response.value.forEach(joke=>{output+=`\n            <li>${joke.joke}</li>\n          `}):output+="<li>Something went wrong</li>",document.querySelector(".output").innerHTML=`<ol>${output}</ol>`}},xhr.send(),e.preventDefault()}document.querySelector("#form").addEventListener("submit",loadJokes);