function loadUsers(e){const gender=document.querySelector("#select").value,http=new easyHTTP;http.get(`https://randomuser.me/api/?gender=${gender}`,(function(err,users){if(err)console.log(err);else{let output="",userObj;JSON.parse(users).results.forEach(user=>{output+=`\n          <tr>\n            <td>${user.name.first}</td>\n            <td><img src="${user.picture.large}" alt="user images" /></td>\n          </tr>`}),document.querySelector(".output").innerHTML=`<table>${output}</table>`}})),e.preventDefault()}document.querySelector("#form").addEventListener("click",loadUsers);