function easyHTTP(){this.http=new XMLHttpRequest}easyHTTP.prototype.get=function(url,callback){this.http.open("GET",url,!0);let self=this;this.http.onload=function(){200===self.http.status?callback(null,self.http.responseText):callback("error "+self.http.responseText)},this.http.send()},easyHTTP.prototype.post=function(url,data,callback){this.http.open("POST",url,!0),this.http.setRequestHeader("Content-type","application/json");let self=this;this.http.onload=function(){callback(null,self.http.responseText)},this.http.send(JSON.stringify(data))},easyHTTP.prototype.put=function(url,data,callback){this.http.open("PUT",url,!0),this.http.setRequestHeader("Content-type","application/json");let self=this;this.http.onload=function(){callback(null,self.http.responseText)},this.http.send(JSON.stringify(data))},easyHTTP.prototype.delete=function(url,callback){this.http.open("DELETE",url,!0);let self=this;this.http.onload=function(){200===self.http.status?callback(null,"Post Deleted"):callback("error "+self.http.responseText)},this.http.send()};