document.querySelector(".customer1").addEventListener("click",(function(){const xhr=new XMLHttpRequest;xhr.open("GET","customer.json",!0),xhr.onload=function(){if(200===this.status){const customer=JSON.parse(this.responseText),output=`\n        <ul>\n          <li>${customer.id}</li>\n          <li>${customer.name}</li>\n          <li>${customer.address}</li>\n          <li>${customer.gender}</li>\n        </ul>\n      `;document.querySelector(".output1").innerHTML=output}},xhr.send()})),document.querySelector(".customer2").addEventListener("click",(function(){const xhr=new XMLHttpRequest;xhr.open("GET","customers.json",!0),xhr.onload=function(){if(200===this.status){const customers=JSON.parse(this.responseText);let output="";customers.forEach(customer=>{output+=`\n          <tr>\n            <td>${customer.id}</td>\n            <td>${customer.name}</td>\n            <td>${customer.address}</td>\n            <td>${customer.gender}</td>\n          </tr>\n        `}),console.log(customers),document.querySelector(".output2").innerHTML=output}},xhr.send()}));